import{SceneManager3D}from"./SceneManager3D.js";import{HexMeshFactory}from"./HexMeshFactory.js";import*as THREE from"three";export class Game3D{constructor(game){this.game=game;this.enabled=false;this.sceneManager=null;this.meshFactory=new HexMeshFactory;this.heroMesh=null}init(containerId){this.sceneManager=new SceneManager3D(containerId);const container=document.getElementById(containerId);container.addEventListener("pointerdown",e=>this.onClick(e));container.addEventListener("pointermove",e=>this.onPointerMove(e))}onPointerMove(event){if(!this.enabled||!this.sceneManager)return;const rect=this.sceneManager.renderer.domElement.getBoundingClientRect();const x=(event.clientX-rect.left)/rect.width*2-1;const y=-((event.clientY-rect.top)/rect.height)*2+1;const hitObject=this.sceneManager.getIntersection(x,y);if(hitObject&&hitObject.userData&&hitObject.userData.type==="hex"){this.sceneManager.setSelectorPosition(hitObject.position);this.sceneManager.renderer.domElement.style.cursor="pointer"}else{this.sceneManager.hideSelector();this.sceneManager.renderer.domElement.style.cursor="default"}}onClick(event){if(!this.enabled||!this.game||!this.sceneManager)return;const rect=this.sceneManager.renderer.domElement.getBoundingClientRect();const x=(event.clientX-rect.left)/rect.width*2-1;const y=-((event.clientY-rect.top)/rect.height)*2+1;const hitObject=this.sceneManager.getIntersection(x,y);if(hitObject&&hitObject.userData){const data=hitObject.userData;if(data.type==="hex"){if(this.game.toggleHexSelection&&typeof this.game.toggleHexSelection==="function"){this.game.toggleHexSelection(data.q,data.r)}else{if(this.game.movementMode){this.game.moveHero(data.q,data.r)}else if(this.game.selectHex){this.game.selectHex(data.q,data.r)}else{console.warn("[3D] No suitable method found to handle hex click")}}}}}toggle(){this.enabled=!this.enabled;const container=document.getElementById("game-container-3d");const canvas2d=document.querySelector(".canvas-layer");if(this.enabled){container.style.display="block";canvas2d.style.display="none";this.sceneManager.onWindowResize();this.sceneManager.start();this.render()}else{container.style.display="none";canvas2d.style.display="block";this.sceneManager.stop()}return this.enabled}render(){if(!this.enabled||!this.sceneManager)return;this.sceneManager.clear();if(this.game.hexGrid){for(const hex of this.game.hexGrid.hexes.values()){const mesh=this.meshFactory.createHexMesh(hex);this.sceneManager.add(mesh)}}this.renderHero()}renderHero(){if(!this.game.hero)return;const geo=new THREE.CylinderGeometry(.3,.3,1.5,8);const mat=new THREE.MeshStandardMaterial({color:16766720,metalness:.8,roughness:.2});this.heroMesh=new THREE.Mesh(geo,mat);const hex=this.game.hexGrid.getHex(this.game.hero.q,this.game.hero.r);if(hex){const size=this.meshFactory.radius*1.05;const x=size*1.5*hex.q;const z=size*Math.sqrt(3)*(hex.r+hex.q/2);this.heroMesh.position.set(x,1,z)}this.sceneManager.add(this.heroMesh);this.sceneManager.add(this.heroMesh)}update(){if(!this.enabled)return;this.render()}}