export function animateCardDraw(cardElement,index=0){return new Promise(resolve=>{cardElement.style.transform="translateX(-200px) translateY(100px) scale(0.5) rotate(-20deg)";cardElement.style.opacity="0";const delay=index*80;setTimeout(()=>{cardElement.style.transition="all 0.6s cubic-bezier(0.34, 1.56, 0.64, 1)";cardElement.style.transform="translateX(0) translateY(0) scale(1) rotate(0deg)";cardElement.style.opacity="1";setTimeout(resolve,600)},delay)})}export function animateCardPlay(cardElement,targetElement){return new Promise(resolve=>{const cardRect=cardElement.getBoundingClientRect();const targetRect=targetElement.getBoundingClientRect();const deltaX=targetRect.left-cardRect.left;const deltaY=targetRect.top-cardRect.top;const clone=cardElement.cloneNode(true);clone.style.position="fixed";clone.style.left=`${cardRect.left}px`;clone.style.top=`${cardRect.top}px`;clone.style.width=`${cardRect.width}px`;clone.style.zIndex="9999";clone.style.pointerEvents="none";document.body.appendChild(clone);cardElement.style.opacity="0";let progress=0;const duration=500;const startTime=performance.now();function animate(currentTime){progress=Math.min((currentTime-startTime)/duration,1);const eased=1-Math.pow(1-progress,3);const arcHeight=-100;const x=deltaX*eased;const y=deltaY*eased+arcHeight*Math.sin(progress*Math.PI);clone.style.transform=`translate(${x}px, ${y}px) rotate(${progress*360}deg) scale(${1-progress*.3})`;clone.style.opacity=`${1-progress*.5}`;if(progress<1){requestAnimationFrame(animate)}else{clone.remove();resolve()}}requestAnimationFrame(animate)})}export function animateCardDiscard(cardElement){return new Promise(resolve=>{cardElement.style.transition="all 0.4s ease-out";cardElement.style.transform="translateY(50px) scale(0.8) rotate(10deg)";cardElement.style.opacity="0";cardElement.style.filter="blur(2px)";setTimeout(()=>{cardElement.remove();resolve()},400)})}export function animate3DTilt(cardElement,mouseX,mouseY){const rect=cardElement.getBoundingClientRect();const cardCenterX=rect.left+rect.width/2;const cardCenterY=rect.top+rect.height/2;const rotateX=(mouseY-cardCenterY)/10;const rotateY=(cardCenterX-mouseX)/10;cardElement.style.transition="transform 0.1s ease-out";cardElement.style.transform=`\n        perspective(1000px) \n        rotateX(${rotateX}deg) \n        rotateY(${rotateY}deg) \n        translateY(-12px) \n        scale(1.08)\n    `}export function reset3DTilt(cardElement){cardElement.style.transition="transform 0.3s ease-out";cardElement.style.transform=""}export function shakeCard(cardElement){return new Promise(resolve=>{cardElement.classList.add("card-shake");setTimeout(()=>{cardElement.classList.remove("card-shake");resolve()},500)})}export function flipCard(cardElement){return new Promise(resolve=>{cardElement.style.transition="transform 0.6s";cardElement.style.transformStyle="preserve-3d";cardElement.style.transform="rotateY(90deg)";setTimeout(()=>{cardElement.style.transform="rotateY(0deg)";setTimeout(()=>{cardElement.style.transformStyle="";resolve()},300)},300)})}export function pulseGlow(cardElement){cardElement.classList.add("card-pulse-glow")}export function stopPulseGlow(cardElement){cardElement.classList.remove("card-pulse-glow")}export function highlightCard(cardElement){return new Promise(resolve=>{cardElement.classList.add("card-highlight");setTimeout(()=>{cardElement.classList.remove("card-highlight");resolve()},1e3)})}export default{animateCardDraw:animateCardDraw,animateCardPlay:animateCardPlay,animateCardDiscard:animateCardDiscard,animate3DTilt:animate3DTilt,reset3DTilt:reset3DTilt,shakeCard:shakeCard,flipCard:flipCard,pulseGlow:pulseGlow,stopPulseGlow:stopPulseGlow,highlightCard:highlightCard};