export class MagicEffects{constructor(engine){this.engine=engine}manaEffect(x,y,color){this.engine.burst(x,y,12,{color:color,speed:2,size:3,decay:.03,gravity:-.05})}manaGlitterEffect(x,y,color){for(let i=0;i<3;i++){this.engine.addParticle(x+(Math.random()-.5)*20,y+(Math.random()-.5)*20,{color:color,speed:.5,size:Math.random()*3,decay:.02,vx:(Math.random()-.5)*.5,vy:(Math.random()-.5)*.5-.5})}}healEffect(x,y){for(let i=0;i<15;i++){this.engine.addParticle(x+(Math.random()-.5)*40,y+(Math.random()-.5)*40,{color:"#4ade80",speed:1,vx:0,vy:-2,size:4,decay:.02})}}cardGlowEffect(x,y,color="#8b5cf6"){this.engine.addParticle(x+(Math.random()-.5)*100,y+(Math.random()-.5)*140,{color:color,speed:.5,size:2,decay:.05})}playCardEffect(x,y,color){for(let i=0;i<30;i++){const angle=Math.random()*Math.PI*2;const dist=Math.random()*50;this.engine.addParticle(x+Math.cos(angle)*dist,y+Math.sin(angle)*dist,{color:color||"#ffffff",vx:Math.cos(angle)*2,vy:Math.sin(angle)*2,size:3,decay:.03})}}}