import{BaseSiteHandler}from"./BaseSiteHandler.js";import{SAMPLE_ADVANCED_ACTIONS}from"../card.js";export class MonasteryHandler extends BaseSiteHandler{getOptions(_site){const options=[];options.push({id:"heal",label:"Heilen (2 Einfluss / Wunde)",action:()=>this.healWounds(2),enabled:this.game.hero.wounds.length>0});const cards=SAMPLE_ADVANCED_ACTIONS;options.push({id:"train",label:"Training (Karten kaufen)",subItems:cards.map(c=>({type:"card",data:c,cost:6,action:()=>this.buyCard(c,6)}))});return options}}