import{BaseSiteHandler}from"./BaseSiteHandler.js";import{SITE_TYPES}from"../sites.js";import{getUnitsForLocation}from"../unit.js";export class VillageHandler extends BaseSiteHandler{getOptions(_site){const options=[];options.push({id:"heal",label:"Heilen (3 Einfluss / Wunde)",action:()=>this.healWounds(3),enabled:this.game.hero.wounds.length>0});const units=getUnitsForLocation(SITE_TYPES.VILLAGE);options.push({id:"recruit",label:"Einheiten rekrutieren",subItems:units.map(u=>({type:"unit",data:u,cost:u.cost,action:()=>this.recruitUnit(u)}))});return options}}